mapper.insert:
insert into user (username, password, name
  )
values (#{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}
  )

mapper.update:
update user
set
username=#{username},
password=#{password},
name=#{name}
where id=#{id}

mapper.select:
select id, username, password, name
from user
where id=#{id}

select id, username, password, name
from user
limit #{start},#{pageSize}
